import React, {Component} from 'react';
import Recipe from '../components/recipe/Recipe'
import {Route, Switch} from 'react-router-dom'
import Day from '../components/day/Day'
import Week from '../components/week/Week'
import {NavLink} from 'react-router-dom';
import Form from '../components/form/Form'

class MealPlans extends Component {
    constructor(props){
        super(props);
        this.styles = {
            pageTitle: {
                fontSize: '1.5rem',
                marginTop: '100px',
                marginBottom: '30px',
                textAlign: 'center'
            },
            container: {
                width: '90%',
                height: '100%',
                marginLeft: 'auto',
                marginRight: 'auto',
                position: 'relative'
            },
            recipes: {
                width: '100%',
                height: '100%',
                display: 'flex',
                flexFlow: 'row',
                flexWrap: 'wrap',
                justifyContent: 'space-between'
            }
        };
        this.state = {
            duration: "day",
            recipes: []
        }
    }
    render() {
        let recipeList  = this.props.features.recipes.map((element, i) => {
            let diet;
            if(element.vegetarian === true) {
                diet = 'Vegetarian'
            } else if(element.vegan === true) {
                diet = 'Vegan'
            } else if(element.glutenFree === true) {
                diet = 'Gluten free'
            } else {
                diet = ''
            }
            return <Recipe key={i} val={element} diet={diet}/>
        });
        return (
            <div style={this.styles.container}>
                <button onClick={function(){
                    this.setState({
                        duration: "day"
                    })
                }.bind(this)}>Day</button>
                <button onClick={function(){
                    this.setState({
                        duration: "week"
                    })
                }.bind(this)}>Week</button>
                <h2 style={this.styles.pageTitle}>Meal Planner</h2>

                <Form getMeals = {this.props.getMeals} name={this.state.duration}/>
                <div style={this.styles.recipes}>
                    {recipeList}
                </div>
            </div>
        );
    }
}

export default MealPlans;
