import React from 'react'
import './Recipe.css'

const Recipe = props => {
    return (
        <article style={styles.card}>
            <div style={styles.imageContainer}>
                <div className="overlay">
                    <h5 className="overlayText">{props.diet}</h5>
                </div>
                <img src={props.val.image} alt={props.val.title} style={styles.image}/>
            </div>
            <h5>Health Score {props.val.healthScore}</h5>
            <h4>{props.val.title}</h4>
            <h5>Servings <p>{props.val.servings}</p></h5>
            <h5>Prep time <p>{props.val.readyInMinutes}</p></h5>
            <h6>{props.diet}</h6>
        </article>
    )
};

export default Recipe

const styles = {
    card: {
        width: '350px',
        height: '425px',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: '10px',
        boxShadow: '3px 6px 8px -3px RGBA(52,48,62,.15)',
        backgroundColor: 'white'
    },
    image: {
        width: '100%',
        boxSizing: 'border-box',
        border: 'red solid 1px'
    },
    imageContainer: {
        position: 'relative',
        width: '90%',
        height: 'auto',
        margin: '5%',
        border: 'red solid 1px'
    }
};
