import React from 'react'
import './Recipe.css'

const Recipe = props => {
    return (
        <article style={styles.card}>
            <div style={styles.imageContainer}>
                <div className="overlay">
                    <h5 className="overlayText">{props.diet}</h5>
                </div>
                <img src={props.val.image} alt={props.val.title} style={styles.image}/>
            </div>
            <h4>{props.val.title}</h4>
            <div style={styles.recipeInfo}>
                <h5 style={styles.label}>Health Score</h5>
                <p>{props.val.healthScore}</p>
                <h5 style={styles.label}>Servings</h5>
                <p>{props.val.servings}</p>
                <h5 style={styles.label}>Prep time</h5>
                <p>{props.val.readyInMinutes}</p>
            </div>
            <h6>{props.diet}</h6>
        </article>
    )
};

export default Recipe

const styles = {
    card: {
        width: '400px',
        height: '400px',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: '10px',
        boxShadow: '3px 6px 8px -3px RGBA(52,48,62,.15)',
        backgroundColor: 'white'
    },
    image: {
        width: '100%',
        boxSizing: 'border-box'
    },
    imageContainer: {
        position: 'relative',
        boxSizing: 'border-box',
        width: '90%',
        height: 'auto',
        margin: '5%'
    },
    label: {
        margin: '0'
    },
    recipeInfo: {
        display: 'flex',
        width: '100%',
        height: 'auto'
    }
};
