import React, {Component} from 'react';
import Recipe from '../components/recipe/Recipe'
import Form from '../components/form/Form'

class RecipeSearch extends Component {
    constructor(props){
        super(props);
        this.styles = {
            pageTitle: {
                fontSize: '1.5rem',
                marginTop: '100px',
                marginBottom: '30px',
                textAlign: 'center'
            },
            container: {
                width: '90%',
                height: '100%',
                marginLeft: 'auto',
                marginRight: 'auto',
                position: 'relative'
            },
            recipes: {
                width: '100%',
                height: '100%',
                display: 'flex',
                flexFlow: 'row',
                flexWrap: 'wrap',
                justifyContent: 'space-between'
            }
        };
        this.state = {
            duration: "day",
            recipes: [],
            nutrition: []
        }
    }

    handleGetMeals = (calories, diet, exclude, name) => {
        let api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/search";

        if(diet.trim() !== "") {
            api = api + "&diet=" + diet;
        }

        if(exclude.trim() !== "") {
            api = api + "&exclude=" + exclude;
        }

        this.props.getMeals(api, function(response){
            this.setState({loaded: true, recipes: response.results});
        }.bind(this));
    };

    render() {
        console.log(this.state.recipes);
        let recipeList  = this.state.recipes.map((element, i) => {
            let diet;
            if(element.vegetarian === true) {
                diet = 'Vegetarian'
            } else if(element.vegan === true) {
                diet = 'Vegan'
            } else if(element.glutenFree === true) {
                diet = 'Gluten free'
            } else {
                diet = ''
            }
            if (element.day) {
                let values = JSON.parse(element.value);
                element.image = "https://spoonacular.com/recipeImages/" + values.id + "-240x150." + values.imageType;
                element.title = values.title;
            } else {
                element.image = "https://spoonacular.com/recipeImages/" + element.id + "-240x150." + element.imageType;
            }
            return <Recipe key={i} val={element} diet={diet}/>
        });
        return (
            <div style={this.styles.container}>
                <h2 style={this.styles.pageTitle}>Recipe Search</h2>
                <h3>(all inputs are optional)</h3>

                <Form formType="Get Recipes" handleGetMeals={this.handleGetMeals} name={this.state.duration} hide={true}/>
                {this.state.loaded ?
                    <div style={this.styles.recipes}>
                        {recipeList}
                    </div>
                    : null}
            </div>
        );
    }
}

export default RecipeSearch;
