import React, {Component} from 'react';
import Recipe from '../components/recipe/Recipe'
import {Route, Switch} from 'react-router-dom'
import Day from '../components/day/Day'
import Week from '../components/week/Week'
import {NavLink} from 'react-router-dom';
import Form from '../components/form/Form'

class MealPlans extends Component {
    constructor(props){
        super(props);
        this.styles = {
            pageTitle: {
                fontSize: '1.5rem',
                marginTop: '100px',
                marginBottom: '30px',
                textAlign: 'center'
            },
            container: {
                width: '90%',
                height: '100%',
                marginLeft: 'auto',
                marginRight: 'auto',
                position: 'relative'
            },
            recipes: {
                width: '100%',
                height: '100%',
                display: 'flex',
                flexFlow: 'row',
                flexWrap: 'wrap',
                justifyContent: 'space-between'
            }
        };
    }
    render() {
        let recipeList  = this.props.features.recipes.map((element, i) => {
            let diet;
            if(element.vegetarian === true) {
                diet = 'Vegetarian'
            } else if(element.vegan === true) {
                diet = 'Vegan'
            } else if(element.glutenFree === true) {
                diet = 'Gluten free'
            } else {
                diet = ''
            }
            return <Recipe key={i} val={element} diet={diet}/>
        });
        return (
            <div style={this.styles.container}>
                <h2 style={this.styles.pageTitle}>Meal Planner</h2>
{/*                <NavLink to="/Day">Day</NavLink>
                <NavLink to="/Week">Week</NavLink>
                <section className="main-contentr" >
                    <Switch>
                        <Route exact path={'/Day'} render={(props) => <Day {...props} isAuthed={true} features={this.props.features} getMeals={this.props.getMeals}/>}/>
                        <Route exact path={'/Week'} render={(props) => <Week {...props} isAuthed={true} getMeals={this.props.getMeals}/>}/>
                    </Switch>
                </section>*/}
                <Form getMeals = {this.props.getMeals} name="day"/>
                <div style={this.styles.recipes}>
                    {recipeList}
                </div>
            </div>
        );
    }
}

export default MealPlans;
