import React from 'react'

const Recipe = props => {
    return (
        <article style={styles.card}>
            <div style={styles.imageContainer}>
                <img src={props.val.image} alt={props.val.title} style={styles.image}/>
                <div style={styles.overlay}>
                    <h5 style={styles.overlayText}>{props.diet}</h5>
                </div>
            </div>
            <h5>Health Score {props.val.healthScore}</h5>
            <h4>{props.val.title}</h4>
            <h5>Servings <p>{props.val.servings}</p></h5>
            <h5>Prep time <p>{props.val.readyInMinutes}</p></h5>
            <h6>{props.diet}</h6>
        </article>
    )
};

export default Recipe

const styles = {
    card: {
        position: 'relative',
        width: '350px',
        height: '425px',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: '10px',
        boxShadow: '3px 6px 8px -3px RGBA(52,48,62,.15)',
        backgroundColor: 'white',
    },
    image: {
        width: '100%'
    },
    imageContainer: {
        width: '90%',
        height: 'auto',
        margin: '5%'
    },
    overlay: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        top: '0',
        backgroundColor: 'RGBA(0,0,0,.3)',
        opacity: '0',

        '&:hover': {
            opacity: '1'
        }
    },
    overlayText: {
        position: 'relative',
        color: 'white',
        marginTop: '30%'
    }
};
