import React, {Component} from 'react';
import Recipe from '../components/recipe/Recipe'

class Landing extends Component {
    constructor(props){
        super(props);
        this.styles = {
            pageTitle: {
                fontSize: '1.5rem',
                marginTop: '100px',
                textAlign: 'center'
            },
            container: {
                width: '100%',
                height: '100%'
            },
            recipes: {
                width: '100%',
                height: '100%',
                display: 'flex',
                justifyContent: 'space-between',
                flexBasis: 'auto'
            }
        };
    }
    render() {
        console.log(this.props.features.recipes);
        let recipeList  = this.props.features.recipes.map((element, i) => {
            let diet;
            if(element.vegetarian === true) {
                diet = 'Vegetarian'
            } else if(element.vegan === true) {
                diet = 'Vegan'
            } else if(element.glutenFree === true) {
                diet = 'Gluten free'
            } else {
                diet = ''
            }
            return <Recipe key={i} val={element} diet={diet}/>
        });
        return (
            <div style={this.styles.container}>
                <h2 style={this.styles.pageTitle}>Featured Recipes</h2>
                <div style={this.styles.recipes}>
                    {recipeList}
                </div>
            </div>
        );
    }
}

export default Landing;
