import React, {Component} from 'react';
import Recipe from '../components/recipe/Recipe'

class Landing extends Component {
    constructor(props){
        super(props);
        this.styles = {
            pageTitle: {
                fontSize: '1.5rem',
                marginTop: '50px',
                marginBottom: '30px',
                textAlign: 'center'
            },
            container: {
                width: '90%',
                height: '100%',
                marginLeft: 'auto',
                marginRight: 'auto',
                position: 'relative'
            },
            recipes: {
                width: '100%',
                height: '100%',
                display: 'flex',
                flexFlow: 'row',
                flexWrap: 'wrap',
                justifyContent: 'space-between'
            }
        };
        this.state = {
            recipes: []
        }
    }

    componentDidMount() {
        let api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/random?number=21";
        //Running getMeals to provide featured recipes for landing page
        this.setState({recipes: this.props.getMeals(api)});
    }

    render() {
        let recipeList  = this.state.recipes.map((element, i) => {
            let diet;
            if(element.vegetarian === true) {
                diet = 'Vegetarian'
            } else if(element.vegan === true) {
                diet = 'Vegan'
            } else if(element.glutenFree === true) {
                diet = 'Gluten free'
            } else {
                diet = ''
            }
            return <Recipe key={i} val={element} diet={diet}/>
        });
        return (
            <div style={this.styles.container}>
                <h2 style={this.styles.pageTitle}>Featured Recipes</h2>
                {this.state.recipes.length > 0 ?
                    <div style={this.styles.recipes}>
                        {recipeList}
                    </div>
                : null}
            </div>
        );
    }
}

export default Landing;
