import React, {Component} from 'react';
import './App.css';

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            duration: '',
            calories: '',
            diet: ''
        }
    }

    getMeals(e) {
        e.preventDefault();
        fetch("https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/mealplans/generate?timeFrame=" +this.state.duration+ "&targetCalories=" +document.getElementById("calories").value+ "&diet=" +document.getElementById("diet").value, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
                "x-rapidapi-key": "a7abd7a9cemshabd18f81b1c14e0p15c8dcjsndf63f02d30eb"
            }
        })
            .then(response => {
                if(response.ok) {
                    return response.json();
                }
                throw response;
            })
            .then(responseAsJson => {
                console.log(responseAsJson)
                let resultsSection = document.getElementById("results");
                for (let i=0; i<responseAsJson.meals.length; i++){
                    let card = document.createElement("article");
                    let title = document.createElement("h2");
                    title.innerHTML = responseAsJson.meals[i].title;
                }
            })
            .catch(err => {
                console.log(err);
                console.log("error")
            });
    }

    render() {
        return (
            <div className="App">
                <form onSubmit={this.getMeals()}>
                    <h1>Generate Meal Plan</h1>
                    <label htmlFor="duration">Duration </label>
                    <input type="radio" id="day" name="duration" value="day" onChange={function() {
                        if (document.getElementById("day").checked) {
                            this.state.duration = "day"
                        }
                    }}/>
                    <label htmlFor="day">Day </label>
                    <input type="radio" id="week" name="duration" value="week" onChange={function() {
                        if (document.getElementById("week").checked) {
                            this.state.duration = "week"
                        }
                    }}/>
                    <label htmlFor="week">Week </label>
                    <label htmlFor="calories">Target Calories </label>
                    <input type="number" min="1" id="calories"/>
                    <label htmlFor="diet">Diet </label>
                    <input type="text" id="diet" placeholder="Vegetarian, Keto..."/>

                    <button type="submit">Get Meal Plan</button>
                </form>

                <div id="results">

                </div>
            </div>
        );
    }

}

export default App;
