import React, {Component} from 'react';
import './App.css';
import Header from "./components/header/Header";
import {BrowserRouter as Router} from 'react-router-dom';
import Routes from "./components/routes/Routes";

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            duration: '',
            calories: '',
            diet: '',
            recipes: [],
            loaded: false
        }
    }

    componentDidMount() {
        this.getMeals();
    }

    getMeals = e => {
        let api;
        let recipes = [];
        if(e) {
            e.preventDefault();
            if(e.target.parentElement.name === "day") {
                api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/mealplans/generate?timeFrame=day" + "&targetCalories=" + this.state.calories + "&diet=" + this.state.diet
            } else {
                api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/mealplans/generate?timeFrame==week" + "&targetCalories=" + this.state.calories + "&diet=" + this.state.diet
            }
        } else {
            api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/random?number=20"
        }
        fetch(api, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
                "x-rapidapi-key": "a7abd7a9cemshabd18f81b1c14e0p15c8dcjsndf63f02d30eb"
            }
        })
            .then((resp) => resp.json())
            .then(function(data) {
                let features = data.results;
                for(let i = 0; i < features.length; i++) {
                    return features.map(feature => {
                        recipes[i].imgSrc = feature.image;
                    })
                }
            })
            .then(function() {
                this.setState({
                    recipes: recipes
                })

            }.bind(this))
            .catch(function(error) {
                console.log(error);
            });
    };

    render() {
        return (
            <div className="App">
                <Header/>
                <Router>
                    <Routes
                    getMeals = {this.getMeals}
                    features = {this.state.recipes}
                    />
                </Router>
            </div>
        );
    }
}

export default App;
