import React from 'react'
import Search from '../search/Search'
import UserID from '../user/UserID'

const Header = props => {
    if(!props.user) {
        return null;
    }
    return (
        <header style={styles.header}>
            <h1 style={styles.logo}>Be <strong style={styles.strong}>Social</strong></h1>
            <Search search={props.search}/>
            <div style={styles.container}>
                <UserID name={props.user.name} imgSrc={props.user.imgSrc}/>
            </div>
        </header>
    )
};

export default Header

const styles = {
    header: {
        position: 'relative',
        display: 'flex',
        flexFlow: 'row',
        padding: '0',
        margin: '0',
        fontSize: '1rem',
        fontWeight: 'bold',
        backgroundColor: '#022634',
        width: '100%',
        height: '10%',
        alignContent: 'center'
    },
    logo: {
        display: 'flex',
        flexFlow:'column',
        color: 'var(--main-color)',
        width: '25%',
        marginTop: '.5%',
        marginLeft: '1%',
        lineHeight: '2rem'
    },
    settings: {
        width: '20%',
        height: '100%',
        display: 'flex',
        flexFlow: 'row',
        justifyContent: 'flex-end',
        alignContent: 'center',
    },
    container: {
        width: '25%',
        display: 'flex',
        flexFlow: 'row',
        alignContent: 'center'
    },
    strong: {
        fontWeight: 'bold',
        fontSize: '3rem',
        color: 'var(--accent-color)'
    },
    icon: {
        width: '30px',
        height: '30px',
        marginTop: 'auto',
        marginBottom: 'auto',
        stroke: 'white',
        fill: 'white',
        backgroundColor: '#8CA4AE',
        borderRadius: '50%'
    },
    iconContainer: {
        width: '30%',
        height: '100%',
    }
};
