import React, {Component} from 'react';
import './App.css';
import {BrowserRouter as Router} from 'react-router-dom';
import Routes from "./components/routes/Routes";
import Nav from "./components/nav/Nav"

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            duration: '',
            calories: '',
            diet: '',
            recipes: [],
            loaded: false
        }
    }

    componentDidMount() {
        //Running getMeals to provide featured recipes for landing page
        this.getMeals("featured");
    }

    //Function to fetch data from API and return recipes as JSON
    getMeals = e => {
        let api;
        if(e !== "featured") {
            e.preventDefault();
            if(e.target.parentElement.name === "day") {
                api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/mealplans/generate?timeFrame=day" + "&targetCalories=" + this.state.calories + "&diet=" + this.state.diet
            } else {
                api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/mealplans/generate?timeFrame==week" + "&targetCalories=" + this.state.calories + "&diet=" + this.state.diet
            }
        } else if(e === "featured") {
            api = "https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/random?number=21"
        } else {
            return null;
        }
        fetch(api, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
                "x-rapidapi-key": "a7abd7a9cemshabd18f81b1c14e0p15c8dcjsndf63f02d30eb"
            }
        })

            .then(response => {
                if(response.ok) {
                    return response.json();
                }
                throw response;
            })
            .then(responseAsJson => {
                this.setState({recipes: responseAsJson, loaded: true});
            })
            .catch(err => {
                console.log(api);
                console.log(err);
            });
    };

    render() {
        return (
            <div className="App">
                {/* Conditional that prevents loading before getMeals has returned the data from the API */}
                {this.state.loaded ?
                    <Router className="content">
                        <Nav/>
                        <Routes
                            getMeals = {this.getMeals}
                            features = {this.state.recipes}
                        />
                    </Router>
                : null}
            </div>
        );
    }
}

export default App;
